import React, { useState } from 'react';

function TaskList() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [editTask, setEditTask] = useState({ id: null, name: '' });

  const addTask = () => {
    if (newTask.trim() !== '') {
      setTasks([...tasks, { id: Date.now(), name: newTask }]);
      setNewTask('');
    }
  };

  const editTaskName = (taskId) => {
    const updatedTasks = tasks.map((task) =>
      task.id === taskId ? { ...task, name: editTask.name } : task
    );
    setTasks(updatedTasks);
    setEditTask({ id: null, name: '' });
  };

  const deleteTask = (taskId) => {
    const updatedTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(updatedTasks);
  };

  return (
    <div>
      <h1> TASK LIST </h1>
      <ul>
        {tasks.map((task) => (
          <li key={task.id}>
            {task.id === editTask.id ? (
              <>
                <input
                  type="text"
                  value={editTask.name}
                  onChange={(e) => setEditTask({ id: task.id, name: e.target.value })}
                />
                <button onClick={() => editTaskName(task.id)}>Save</button>
              </>
            ) : (
              <>
                {task.name}
                <button onClick={() => setEditTask({ id: task.id, name: task.name })}>
                  Edit
                </button>
                <button onClick={() => deleteTask(task.id)}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          placeholder="New Task"
          value={newTask}
          onChange={(e) => setNewTask(e.target.value)}
        />
        <button onClick={addTask}>Add Task</button>
      </div>
    </div>
  );
}

export default TaskList;
